INSERT INTO `languages` (`category`, `desc`, `indx`, `name`, `script`) VALUES ('standard', 'Elvish is fluid, with subtle intonations and intricate grammar. Elven literature is rich and varied, and their songs and poems are famous among other races. Many bards learn their language so they can add Elvish ballads to their repertoires.', 'elvish', 'Elvish', 'ELVISH'), ('exotic', '', 'celestial', 'Celestial', 'CELESTIAL'), ('exotic', '', 'primordial', 'Primordial', 'DWARVISH'), ('exotic', '', 'undercommon', 'Undercommon', 'ELVISH'), ('exotic', '', 'abyssal', 'Abyssal', 'INFERNAL'), ('standard', 'Orc is a harsh, grating language with hard consonants. It has no script of its own but is written in the Dwarvish script.', 'orc', 'Orc', 'DWARVISH'), ('standard', '', 'giant', 'Giant', 'DWARVISH'), ('standard', '', 'goblin', 'Goblin', 'DWARVISH'), ('standard', 'Dwarvish is full of hard consonants and guttural sounds.', 'dwarvish', 'Dwarvish', 'DWARVISH'), ('exotic', 'Draconic is thought to be one of the oldest languages and is often used in the study of magic. The language sounds harsh to most other creatures and includes numerous hard consonants and sibilants.', 'draconic', 'Draconic', 'DRACONIC'), ('standard', 'The Gnomish language, which uses the Dwarvish script, is renowned for its technical treatises and its catalogs of knowledge about the natural world.', 'gnomish', 'Gnomish', 'DWARVISH'), ('exotic', '', 'infernal', 'Infernal', 'INFERNAL'), ('standard', 'The Halfling language isn''t secret, but halflings are loath to share it with others. They write very little, so they don''t have a rich body of literature. Their oral tradition, however, is very strong.', 'halfling', 'Halfling', 'COMMON'), ('standard', '', 'common', 'Common', 'COMMON'), ('exotic', '', 'deep-speech', 'Deep Speech', ''), ('exotic', '', 'sylvan', 'Sylvan', 'ELVISH');
