-- Seed data to the database.
INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('str', 'STR', 'Strength', '["Strength measures bodily power, athletic training, and the extent to which you can exert raw physical force.","A Strength check can model any attempt to lift, push, pull, or break something, to force your body through a space, or to otherwise apply brute force to a situation. The Athletics skill reflects aptitude in certain kinds of Strength checks."]');INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('dex', 'DEX', 'Dexterity', '["Dexterity measures agility, reflexes, and balance.","A Dexterity check can model any attempt to move nimbly, quickly, or quietly, or to keep from falling on tricky footing. The Acrobatics, Sleight of Hand, and Stealth skills reflect aptitude in certain kinds of Dexterity checks."]');INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('con', 'CON', 'Constitution', '["Constitution measures health, stamina, and vital force.","Constitution checks are uncommon, and no skills apply to Constitution checks, because the endurance this ability represents is largely passive rather than involving a specific effort on the part of a character or monster."]');INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('int', 'INT', 'Intelligence', '["Intelligence measures mental acuity, accuracy of recall, and the ability to reason.","An Intelligence check comes into play when you need to draw on logic, education, memory, or deductive reasoning. The Arcana, History, Investigation, Nature, and Religion skills reflect aptitude in certain kinds of Intelligence checks."]');INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('wis', 'WIS', 'Wisdom', '["Wisdom reflects how attuned you are to the world around you and represents perceptiveness and intuition.","A Wisdom check might reflect an effort to read body language, understand someone''s feelings, notice things about the environment, or care for an injured person. The Animal Handling, Insight, Medicine, Perception, and Survival skills reflect aptitude in certain kinds of Wisdom checks."]');INSERT INTO `ability_scores` (`indx`, `name`, `full_name`, `desc`) VALUES ('cha', 'CHA', 'Charisma', '["Charisma measures your ability to interact effectively with others. It includes such factors as confidence and eloquence, and it can represent a charming or commanding personality.","A Charisma check might arise when you try to influence or entertain others, when you try to make an impression or tell a convincing lie, or when you are navigating a tricky social situation. The Deception, Intimidation, Performance, and Persuasion skills reflect aptitude in certain kinds of Charisma checks."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('acrobatics', 'Acrobatics', '["Your Dexterity (Acrobatics) check covers your attempt to stay on your feet in a tricky situation, such as when you''re trying to run across a sheet of ice, balance on a tightrope, or stay upright on a rocking ship''s deck. The GM might also call for a Dexterity (Acrobatics) check to see if you can perform acrobatic stunts, including dives, rolls, somersaults, and flips."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('animal-handling', 'Animal Handling', '["When there is any question whether you can calm down a domesticated animal, keep a mount from getting spooked, or intuit an animal''s intentions, the GM might call for a Wisdom (Animal Handling) check. You also make a Wisdom (Animal Handling) check to control your mount when you attempt a risky maneuver."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('arcana', 'Arcana', '["Your Intelligence (Arcana) check measures your ability to recall lore about spells, magic items, eldritch symbols, magical traditions, the planes of existence, and the inhabitants of those planes."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('athletics', 'Athletics', '["Your Strength (Athletics) check covers difficult situations you encounter while climbing, jumping, or swimming."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('deception', 'Deception', '["Your Charisma (Deception) check determines whether you can convincingly hide the truth, either verbally or through your actions. This deception can encompass everything from misleading others through ambiguity to telling outright lies. Typical situations include trying to fast- talk a guard, con a merchant, earn money through gambling, pass yourself off in a disguise, dull someone''s suspicions with false assurances, or maintain a straight face while telling a blatant lie."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('history', 'History', '["Your Intelligence (History) check measures your ability to recall lore about historical events, legendary people, ancient kingdoms, past disputes, recent wars, and lost civilizations."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('insight', 'Insight', '["Your Wisdom (Insight) check decides whether you can determine the true intentions of a creature, such as when searching out a lie or predicting someone''s next move. Doing so involves gleaning clues from body language, speech habits, and changes in mannerisms."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('intimidation', 'Intimidation', '["When you attempt to influence someone through overt threats, hostile actions, and physical violence, the GM might ask you to make a Charisma (Intimidation) check. Examples include trying to pry information out of a prisoner, convincing street thugs to back down from a confrontation, or using the edge of a broken bottle to convince a sneering vizier to reconsider a decision."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('investigation', 'Investigation', '["When you look around for clues and make deductions based on those clues, you make an Intelligence (Investigation) check. You might deduce the location of a hidden object, discern from the appearance of a wound what kind of weapon dealt it, or determine the weakest point in a tunnel that could cause it to collapse. Poring through ancient scrolls in search of a hidden fragment of knowledge might also call for an Intelligence (Investigation) check."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('medicine', 'Medicine', '["A Wisdom (Medicine) check lets you try to stabilize a dying companion or diagnose an illness."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('nature', 'Nature', '["Your Intelligence (Nature) check measures your ability to recall lore about terrain, plants and animals, the weather, and natural cycles."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('perception', 'Perception', '["Your Wisdom (Perception) check lets you spot, hear, or otherwise detect the presence of something. It measures your general awareness of your surroundings and the keenness of your senses. For example, you might try to hear a conversation through a closed door, eavesdrop under an open window, or hear monsters moving stealthily in the forest. Or you might try to spot things that are obscured or easy to miss, whether they are orcs lying in ambush on a road, thugs hiding in the shadows of an alley, or candlelight under a closed secret door."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('performance', 'Performance', '["Your Charisma (Performance) check determines how well you can delight an audience with music, dance, acting, storytelling, or some other form of entertainment."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('persuasion', 'Persuasion', '["When you attempt to influence someone or a group of people with tact, social graces, or good nature, the GM might ask you to make a Charisma (Persuasion) check. Typically, you use persuasion when acting in good faith, to foster friendships, make cordial requests, or exhibit proper etiquette. Examples of persuading others include convincing a chamberlain to let your party see the king, negotiating peace between warring tribes, or inspiring a crowd of townsfolk."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('religion', 'Religion', '["Your Intelligence (Religion) check measures your ability to recall lore about deities, rites and prayers, religious hierarchies, holy symbols, and the practices of secret cults."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('sleight-of-hand', 'Sleight of Hand', '["Whenever you attempt an act of legerdemain or manual trickery, such as planting something on someone else or concealing an object on your person, make a Dexterity (Sleight of Hand) check. The GM might also call for a Dexterity (Sleight of Hand) check to determine whether you can lift a coin purse off another person or slip something out of another person''s pocket."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('stealth', 'Stealth', '["Make a Dexterity (Stealth) check when you attempt to conceal yourself from enemies, slink past guards, slip away without being noticed, or sneak up on someone without being seen or heard."]');INSERT INTO `skills` (`indx`, `name`, `desc`) VALUES ('survival', 'Survival', '["The GM might ask you to make a Wisdom (Survival) check to follow tracks, hunt wild game, guide your group through frozen wastelands, identify signs that owlbears live nearby, predict the weather, or avoid quicksand and other natural hazards."]');INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('barbarian', 'Barbarian', 12);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('bard', 'Bard', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('cleric', 'Cleric', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('druid', 'Druid', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('fighter', 'Fighter', 10);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('monk', 'Monk', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('paladin', 'Paladin', 10);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('ranger', 'Ranger', 10);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('rogue', 'Rogue', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('sorcerer', 'Sorcerer', 6);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('warlock', 'Warlock', 8);INSERT INTO `classes` (`indx`, `name`, `hit_die`) VALUES ('wizard', 'Wizard', 6);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('dwarf', 'Dwarf', 25);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('elf', 'Elf', 30);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('halfling', 'Halfling', 25);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('human', 'Human', 30);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('dragonborn', 'Dragonborn', 30);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('gnome', 'Gnome', 25);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('half-elf', 'Half-Elf', 30);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('half-orc', 'Half-Orc', 30);INSERT INTO `races` (`indx`, `name`, `speed`) VALUES ('tiefling', 'Tiefling', 30);INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('common', 'Common', '', 'STANDARD', 'Common');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('dwarvish', 'Dwarvish', 'Dwarvish is full of hard consonants and guttural sounds.', 'STANDARD', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('elvish', 'Elvish', 'Elvish is fluid, with subtle intonations and intricate grammar. Elven literature is rich and varied, and their songs and poems are famous among other races. Many bards learn their language so they can add Elvish ballads to their repertoires.', 'STANDARD', 'Elvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('giant', 'Giant', '', 'STANDARD', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('gnomish', 'Gnomish', 'The Gnomish language, which uses the Dwarvish script, is renowned for its technical treatises and its catalogs of knowledge about the natural world.', 'STANDARD', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('goblin', 'Goblin', '', 'STANDARD', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('halfling', 'Halfling', 'The Halfling language isn''t secret, but halflings are loath to share it with others. They write very little, so they don''t have a rich body of literature. Their oral tradition, however, is very strong.', 'STANDARD', 'Common');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('orc', 'Orc', 'Orc is a harsh, grating language with hard consonants. It has no script of its own but is written in the Dwarvish script.', 'STANDARD', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('abyssal', 'Abyssal', '', 'EXOTIC', 'Infernal');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('celestial', 'Celestial', '', 'EXOTIC', 'Celestial');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('draconic', 'Draconic', 'Draconic is thought to be one of the oldest languages and is often used in the study of magic. The language sounds harsh to most other creatures and includes numerous hard consonants and sibilants.', 'EXOTIC', 'Draconic');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('deep-speech', 'Deep Speech', 'An eerie tongue, Deep Speech is a clicking language with unsettling vocalizations that can almost sound like noise. Vibrating, creaking, and trembling sounds complement this mode of communication, making it hard for humanoid vocal cords to mimic.', 'EXOTIC', 'Other');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('infernal', 'Infernal', '', 'EXOTIC', 'Infernal');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('primordial', 'Primordial', '', 'EXOTIC', 'Dwarvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('sylvan', 'Sylvan', '', 'EXOTIC', 'Elvish');INSERT INTO `languages` (`indx`, `name`, `desc`, `language_type`, `script`) VALUES ('undercommon', 'Undercommon', '', 'EXOTIC', 'Elvish');
